!function (l) {
    "use strict";

    // Ensure the sidebar is closed by default in mobile view on page load
    function setSidebarDefaultState() {
        if (l(window).width() < 768) {
            l("body").addClass("sidebar-toggled");
            l(".sidebar").addClass("toggled");
            l(".sidebar .collapse").collapse("hide");
        } else {
            l("body").removeClass("sidebar-toggled");
            l(".sidebar").removeClass("toggled");
        }
    }

    // Set the default state on page load
    setSidebarDefaultState();

    // Sidebar toggle click event
    l("#sidebarToggle, #sidebarToggleTop").on("click", function (e) {
        l("body").toggleClass("sidebar-toggled");
        l(".sidebar").toggleClass("toggled");
        if (l(".sidebar").hasClass("toggled")) {
            l(".sidebar .collapse").collapse("hide");
        }
    });

    // Adjust sidebar state on window resize
    l(window).resize(function () {
        if (l(window).width() < 768) {
            l(".sidebar .collapse").collapse("hide");
        }
        if (l(window).width() < 480 && !l(".sidebar").hasClass("toggled")) {
            l("body").addClass("sidebar-toggled");
            l(".sidebar").addClass("toggled");
            l(".sidebar .collapse").collapse("hide");
        } else if (l(window).width() >= 768) {
            l("body").removeClass("sidebar-toggled");
            l(".sidebar").removeClass("toggled");
        }
    });

    // Prevent scrolling in fixed-nav sidebar on larger screens
    l("body.fixed-nav .sidebar").on("mousewheel DOMMouseScroll wheel", function (e) {
        var o;
        if (l(window).width() > 768) {
            o = e.originalEvent.wheelDelta || -e.originalEvent.detail;
            this.scrollTop += 30 * (o < 0 ? 1 : -1);
            e.preventDefault();
        }
    });

    // Show/hide scroll-to-top button on scroll
    l(document).on("scroll", function () {
        if (l(this).scrollTop() > 100) {
            l(".scroll-to-top").fadeIn();
        } else {
            l(".scroll-to-top").fadeOut();
        }
    });

    // Smooth scrolling to top
    l(document).on("click", "a.scroll-to-top", function (e) {
        var o = l(this);
        l("html, body").stop().animate({
            scrollTop: l(o.attr("href")).offset().top
        }, 1000, "easeInOutExpo");
        e.preventDefault();
    });
}(jQuery);

